DO $$
BEGIN
    -- Cria a tabela se ela n√£o existir
    IF NOT EXISTS (SELECT FROM information_schema.tables WHERE table_name = 'keep_alive') THEN
        CREATE TABLE keep_alive (
            id SERIAL PRIMARY KEY,
            ping_count INTEGER NOT NULL DEFAULT 0,
            last_ping_time TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP
        );

        -- Insere o registro inicial
        INSERT INTO keep_alive (id, ping_count, last_ping_time)
        VALUES (1, 1, CURRENT_TIMESTAMP);
    ELSE
        -- Atualiza o registro existente
        UPDATE keep_alive
        SET ping_count = ping_count + 1,
            last_ping_time = CURRENT_TIMESTAMP
        WHERE id = 1;
    END IF;
END $$;

-- Retorna o n√∫mero total de pings e o hor√°rio do √∫ltimo ping
SELECT ping_count, last_ping_time FROM keep_alive WHERE id = 1;

